mapper.select:
select * from( select *, @rownum:=@rownum+1 num from( select @row_no:=IF(@prev_val =b.category_number,@row_no+1,1)AS rownum,@prev_val := b.category_number as category_number,product_number,product_name,product_stock,product_price,product_desc ,product_cnt,product_date from goodslist b, (select @rownum :=0)z, (select @row_no := 0)x, (select @prev_val :='')y where category_number=#{category_number}
and title like concat('%',#{keyword},'%')
and content like concat('%',#{keyword},'%')
OR title like concat('%',null,'%') OR content like concat('%',null,'%')
order by rownum desc )as goods)as goods
where num> (#{pageNum}-1)*#{amount} and num<=#{pageNum}*#{amount}

mapper.update:
update goodslist
set product_cnt=product_cnt+1
where product_number = ${product_number}

   update goodslist
	   set product_name=#{product_name},
		   product_desc=#{product_desc}
   where product_number=#{product_number}
