mapper.select:
select * from( select *, @rownum:=@rownum+1 num from( select @row_no:=IF(@prev_val =b.support_number,@row_no+1,1)AS rownum,@prev_val := b.support_number as support_number,bno,title,content,regdate,cnt,id,name from supportlist b, (select @rownum :=0)z, (select @row_no := 0)x, (select @prev_val :='')y where support_number=#{support_number}
and title like concat('%',#{keyword},'%')
and content like concat('%',#{keyword},'%')
OR title like concat('%',null,'%') OR content like concat('%',null,'%')
order by rownum desc )as support)as support
where num> (#{pageNum}-1)*#{amount} and num<=#{pageNum}*#{amount}

mapper.update:
update supportlist
set cnt=cnt+1
where bno = ${bno}

   update supportlist
	   set title=#{title},
		   content=#{content}
   where bno=#{bno}
